class MakeFriendship{constructor(t){this.toggler=t,console.log($(this.toggler)),this.toggleFriendship()}toggleFriendship(){$(this.toggler).click((function(t){t.preventDefault();let e=this,o=$(e).attr("id").split("-")[1];console.log(o),$.ajax({type:"post",url:"/friendship/toggle/",data:{id:o},success:function(t){1==t.data.deleted?$(e).html("Add as friend"):$(e).html("Remove")},error:function(t){console.log(t)}})}))}}